kickstart.post:
# setup encryption key to talk to the other agent in the cluster encryption=`cat /etc/411-security/shared.key |grep -v BEGIN |grep -v END | base64 -d |dd bs=1 count=16 |base64`
{ "encrypt_key": "$encryption" }
# only frontend needs to be stateful (event lost while the daemon # was down will be replayed when it is back up) # all other nodes if they lose it is ok so no need to snapshot mkdir -p /var/opt/rocks/serf/
private_ip=`/opt/rocks/bin/rocks list host interface &hostname; output-col=subnet,iface,ip | \ /bin/awk '{if ($1 == "private" && $2 !~ /vlan[0-9]*/ && $4 ~ /-*/) {print $3}}'`
{ "snapshot_path": "/var/opt/rocks/serf/serf.snap", "rejoin_after_leave": true, "replay_on_join": true, "bind": "$private_ip", "retry_join": ["&Kickstart_PrivateHostname;.&Kickstart_PrivateDNSDomain;"] }
if [ -x /etc/init.d/rocks-serf ]; then /sbin/chkconfig rocks-serf on else /usr/bin/systemctl enable rocks-serf fi
